    %arg0: tensor<2xi32> {tf._user_specified_name = "args_0"},
    %arg1: tensor<2xi32> {tf._user_specified_name = "args_1"}) -> tensor<2xi32>
    attributes {
      iree.module.export,
      iree.reflection = {
        abi = "sip",
