@@ -179,7 +179,7 @@
       maxVal = 1;

     int maxLog = log10(maxVal);
-    maxLog = (maxLog > 0) ? floor(maxLog) + 1 : 1;
+    maxLog = (maxLog > 0) ? std::floor(maxLog) + 1 : 1;
     const int padding = 4;
     convert.width(convert.precision() + maxLog + padding);
     printVal.raw_print(convert);
