<denchmark-link:https://cloud.githubusercontent.com/assets/4621239/9134840/7733c1b2-3d08-11e5-81b4-c2a4da2135ef.png></denchmark-link>

   #include < iostream >
   #include < opencv2/opencv.hpp >
   using namespace std;
   using namespace cv;
   int main()
   {
       // Disable OPENCL here
       cv::ocl::setUseOpenCL(false);
       CascadeClassifier test("/home/spu/Documents/github/opencv/data/haarcascades/haarcascade_eye_tree_eyeglasses.xml");
       if(test.empty()){
           cerr << "cascade not loaded" << endl;
           return 0;
        }
        Mat image = imread("/data/glasses.jpg");
        Mat image_gray;
        cvtColor(image, image_gray, COLOR_BGR2GRAY);
        equalizeHist(image_gray, image_gray);
        vector< Rect > eyes;
        test.detectMultiScale(image_gray, eyes, 1.1, 0);
        for(size_t i=0; i < eyes.size(); i++){
           rectangle(image, eyes[i], Scalar(255,0,0), 1);
        }
        imshow("result", image); waitKey(0);
        return 0;
    }
