class FCMaskedActionsModel(TorchModelV2, nn.Module):
def __init__(self, obs_space, action_space, num_outputs, model_config, name): nn.Module.__init__(self) super(FCMaskedActionsModel, self).__init__(obs_space, action_space, action_space.n, model_config, name) true_obs_space = gym.spaces.MultiBinary(n=obs_space.shape[0] - action_space.n) self.action_model = FullyConnectedNetwork( obs_space=true_obs_space, action_space=action_space, num_outputs=action_space.n, model_config=model_config, name=name + "action_model")
def forward(self, input_dict, state, seq_lens): # Extract the available actions tensor from the observation. action_mask = input_dict["obs"]["action_mask"] # Compute the predicted action embedding
raw_actions, state = self.action_model({ "obs": input_dict["obs"]["real_obs"] }) inf_mask = torch.clamp(torch.log(action_mask), min=-1e10) actions = raw_actions + inf_mask
return actions, state
def value_function(self): return self.action_model.value_function()
class FAKEEnv(gym.Env):
def __init__(self, env_config): self.problem_size = 210 # <-- changed this here, all seems ok self.action_space = gym.spaces.Discrete(self.problem_size + 1) self.observation_space = gym.spaces.Dict({ "action_mask": gym.spaces.Box(0, 1, shape=(self.problem_size + 1,)), "real_obs": gym.spaces.Box(low=0.0, high=1.0, shape=(self.problem_size * 7,), dtype=np.float), }) self.i = 0
def reset(self): self.i = 0 self.legal_actions = np.ones((self.action_space.n,), dtype=np.int) self.state = np.zeros((self.problem_size, 7), dtype=np.float) return self._get_current_state_representation()
def _get_current_state_representation(self): self.state[:, 0] = self.legal_actions[:-1] return { "real_obs": self.state.flatten(), "action_mask": self.legal_actions, }
def step(self, action: int): self.i += 1 return self._get_current_state_representation(), 0, self.i > self.problem_size, {}
def render(self, mode='human'): pass
def fake_env_creator(env_config): return FAKEEnv(env_config)
register_env("fake", fake_env_creator)
def train_func(): ModelCatalog.register_custom_model("fc_masked_model", FCMaskedActionsModel) # wandb.init(config=default_config) config = default_config config['model'] = { "fcnet_activation": "relu", "custom_model": "fc_masked_model", 'fcnet_hiddens': [config['layer_size'] for k in range(config['layer_nb'])], } config['env_config'] = { 'instance_path': config['instance_path'] }
config['train_batch_size'] = config['num_workers'] * config['num_envs_per_worker'] * config[ 'rollout_fragment_length'] config = with_common_config(config)
config.pop('instance_path', None) config.pop('layer_size', None) config.pop('layer_nb', None)
